<template>
  <div>
    <swiper ref="myswiper">
      <div class="swiper-slide" v-for="data in Carousel" :key="data.bannerId">
        <img :src="data.imgurl" />
      </div>
    </swiper>
    <filmHeader :class="isFixed ? 'fixed' : ''"></filmHeader>
    <router-view></router-view>
  </div>
</template>
<script>
import swiper from '@/views/film/swiper'
import filmHeader from '@/views/film/filmHeader'
export default {
  components: {
    swiper, // 引入元件後要註冊
    filmHeader
  },
  data() {
    return {
      Carousel: [
        {
          bannerId: 1,
          imgurl:
            'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTZau2MwmH79BRUwRjoyQTHerWzRDOWPSGePQ&usqp=CAU'
        },
        {
          bannerId: 2,
          imgurl:
            'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ00f4K5Qdh8-VhD_qAxFw4ZE-SWMcdDDO9HA&usqp=CAU'
        },
        {
          bannerId: 3,
          imgurl:
            'data:image/jpeg;base64,'
        }
      ],
      isFixed: false,
      id: localStorage.getItem('cityId')
    }
  },
  mounted() {
    window.onscroll = this.handleScroll
  },
  methods: {
    // 滾動的座標 與 ref的offsetHeight做比較 ((得到dom節點物件的高度))
    handleScroll() {
      if (
        document.documentElement.scrollTop >=
        this.$refs.myswiper.$el.offsetHeight
      ) {
        this.isFixed = true
      } else {
        this.isFixed = false
      }
    }
  },
  destroyed() {
    window.onscroll = null
  }
}
</script>
<style lang="scss" scoped>
.fixed {
  position: fixed;
  left: 0px;
  top: 0px;
  width: 100%;
  height: 40px;
}
</style>
